client/src/components/competition-card.t.content:
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Calendar, MapPin, Users, Coins } from "lucide-react";
import { Link } from "wouter";

interface CompetitionCardProps {
  id: string;
  name: string;
  date: string;
  venue: string;
  pegsTotal: number;
  pegsAvailable: number;
  entryFee: string;
  prizePool?: string;
  status: "upcoming" | "live" | "completed";
  imageUrl?: string;
}

export function CompetitionCard({
  id,
  name,
  date,
  venue,
  pegsTotal,
  pegsAvailable,
  entryFee,
  prizePool,
  status,
  imageUrl,
}: CompetitionCardProps) {
  const statusColors = {
    upcoming: "bg-accent text-accent-foreground",
    live: "bg-chart-4 text-white animate-pulse",
    completed: "bg-muted text-muted-foreground",
  };

  const statusLabels = {
    upcoming: "Upcoming",
    live: "Live Now",
    completed: "Completed",
  };

  return (
    <Card className="overflow-hidden hover-elevate transition-all duration-200" data-testid={`card-competition-${name}`}>
      <div className="relative h-48 bg-gradient-to-br from-primary/20 to-chart-2/20 overflow-hidden">
        {imageUrl ? (
          <img src={imageUrl} alt={name} className="w-full h-full object-cover" />
        ) : (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-primary/20">
              <MapPin className="h-20 w-20" />
            </div>
          </div>
        )}
        <div className="absolute top-3 right-3">
          <Badge className={statusColors[status]} data-testid={`badge-status-${status}`}>
            {statusLabels[status]}
          </Badge>
        </div>
      </div>

      <CardHeader className="pb-3">
        <h3 className="text-xl font-bold line-clamp-1" data-testid="text-competition-name">{name}</h3>
      </CardHeader>

      <CardContent className="space-y-3">
        <div className="flex items-center gap-2 text-sm text-muted-foreground">
          <Calendar className="h-4 w-4" />
          <span data-testid="text-date">{date}</span>
        </div>
        <div className="flex items-center gap-2 text-sm text-muted-foreground">
          <MapPin className="h-4 w-4" />
          <span className="line-clamp-1" data-testid="text-venue">{venue}</span>
        </div>
        <div className="flex items-center gap-2 text-sm text-muted-foreground">
          <Users className="h-4 w-4" />
          <span data-testid="text-pegs">
            {pegsAvailable} / {pegsTotal} pegs available
          </span>
        </div>
        {prizePool && (
          <div className="flex items-center gap-2 text-sm font-medium text-chart-3">
            <Coins className="h-4 w-4" />
            <span data-testid="text-prize-pool">{prizePool} Prize Pool</span>
          </div>
        )}
      </CardContent>

      <CardFooter className="flex items-center justify-between gap-2 pt-3">
        <div className="text-lg font-bold" data-testid="text-entry-fee">{entryFee}</div>
        <Link href={`/competition/${id}`} asChild>
          <Button
            variant={status === "live" ? "default" : "secondary"}
            disabled={status === "completed" || pegsAvailable === 0}
            data-testid="button-view-details"
          >
            {status === "completed" ? "View Results" : pegsAvailable === 0 ? "Sold Out" : "View Details"}
          </Button>
        </Link>
      </CardFooter>
    </Card>
  );
}

client/src/pages/gallery.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Tabs, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Image, Trophy, Fish, Calendar } from "lucide-react";
import type { GalleryImage } from "@shared/schema";

export default function Gallery() {
  const [selectedImage, setSelectedImage] = useState<GalleryImage | null>(null);
  const [activeTab, setActiveTab] = useState<"all" | "event" | "catch">("all");

  const { data: galleryImages = [], isLoading } = useQuery<GalleryImage[]>({
    queryKey: ["/api/gallery"],
  });

  const filteredImages = galleryImages.filter((img) => {
    if (activeTab === "all") return true;
    return img.category === activeTab;
  });

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-4xl md:text-5xl font-bold mb-4" data-testid="text-gallery-title">
            Gallery
          </h1>
          <p className="text-lg text-muted-foreground">
            Relive the best moments from our competitions - from epic catches to unforgettable events
          </p>
        </div>

        <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as typeof activeTab)} className="mb-8">
          <TabsList data-testid="tabs-gallery-filter">
            <TabsTrigger value="all" data-testid="tab-all">
              <Image className="h-4 w-4 mr-2" />
              All Photos
            </TabsTrigger>
            <TabsTrigger value="event" data-testid="tab-events">
              <Calendar className="h-4 w-4 mr-2" />
              Events
            </TabsTrigger>
            <TabsTrigger value="catch" data-testid="tab-catches">
              <Fish className="h-4 w-4 mr-2" />
              Big Catches
            </TabsTrigger>
          </TabsList>
        </Tabs>

        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {[1, 2, 3, 4, 5, 6, 7, 8].map((i) => (
              <Card key={i} className="overflow-hidden">
                <Skeleton className="aspect-[4/3] w-full" />
                <div className="p-4">
                  <Skeleton className="h-5 w-3/4 mb-2" />
                  <Skeleton className="h-4 w-full mb-1" />
                  <Skeleton className="h-4 w-5/6" />
                </div>
              </Card>
            ))}
          </div>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {filteredImages.map((image) => (
                <Card
                  key={image.id}
                  className="group overflow-hidden cursor-pointer hover-elevate active-elevate-2"
                  onClick={() => setSelectedImage(image)}
                  data-testid={`card-gallery-${image.id}`}
                >
                  <div className="relative aspect-[4/3] overflow-hidden">
                    <img
                      src={image.url}
                      alt={image.title}
                      className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110"
                    />
                    <div className="absolute top-2 right-2">
                      <Badge variant={image.category === "catch" ? "default" : "secondary"}>
                        {image.category === "catch" ? (
                          <>
                            <Fish className="h-3 w-3 mr-1" />
                            Catch
                          </>
                        ) : (
                          <>
                            <Calendar className="h-3 w-3 mr-1" />
                            Event
                          </>
                        )}
                      </Badge>
                    </div>
                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity" />
                  </div>
                  <div className="p-4">
                    <h3 className="font-semibold mb-1 line-clamp-1" data-testid={`text-gallery-title-${image.id}`}>
                      {image.title}
                    </h3>
                    <p className="text-sm text-muted-foreground line-clamp-2">{image.description}</p>
                    {image.weight && (
                      <div className="flex items-center gap-2 mt-2">
                        <Trophy className="h-4 w-4 text-primary" />
                        <span className="text-sm font-semibold">{image.weight}</span>
                      </div>
                    )}
                  </div>
                </Card>
              ))}
            </div>

            {filteredImages.length === 0 && (
              <div className="text-center py-16">
                <Image className="h-16 w-16 mx-auto text-muted-foreground mb-4" />
                <h3 className="text-xl font-semibold mb-2">No photos found</h3>
                <p className="text-muted-foreground">Try adjusting your filter selection</p>
              </div>
            )}
          </>
        )}
      </div>

      <Dialog open={!!selectedImage} onOpenChange={() => setSelectedImage(null)}>
        <DialogContent className="max-w-4xl" data-testid="dialog-gallery-detail">
          {selectedImage && (
            <>
              <DialogHeader>
                <DialogTitle data-testid="text-dialog-title">{selectedImage.title}</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div className="relative aspect-video overflow-hidden rounded-md">
                  <img
                    src={selectedImage.url}
                    alt={selectedImage.title}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="space-y-3">
                  <p className="text-muted-foreground">{selectedImage.description}</p>
                  <div className="flex flex-wrap gap-4 text-sm">
                    <div className="flex items-center gap-2">
                      <Calendar className="h-4 w-4 text-muted-foreground" />
                      <span data-testid="text-dialog-date">{selectedImage.date}</span>
                    </div>
                    {selectedImage.competition && (
                      <div className="flex items-center gap-2">
                        <Trophy className="h-4 w-4 text-muted-foreground" />
                        <span data-testid="text-dialog-competition">{selectedImage.competition}</span>
                      </div>
                    )}
                  </div>
                  {selectedImage.category === "catch" && selectedImage.angler && (
                    <div className="flex items-center gap-4 p-4 bg-muted rounded-md">
                      <div className="flex-1">
                        <p className="text-sm text-muted-foreground">Angler</p>
                        <p className="font-semibold" data-testid="text-dialog-angler">
                          {selectedImage.angler}
                        </p>
                      </div>
                      {selectedImage.weight && (
                        <div className="flex-1">
                          <p className="text-sm text-muted-foreground">Weight</p>
                          <p className="font-semibold text-primary" data-testid="text-dialog-weight">
                            {selectedImage.weight}
                          </p>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/sponsors.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { ExternalLink, Star, Award, Handshake } from "lucide-react";
import { SiInstagram, SiFacebook, SiX } from "react-icons/si";
import type { Sponsor } from "@shared/schema";

export default function Sponsors() {
  const { data: sponsors = [], isLoading } = useQuery<Sponsor[]>({
    queryKey: ["/api/sponsors"],
  });

  const getTierInfo = (tier: string) => {
    switch (tier) {
      case "platinum":
        return { label: "Platinum", color: "bg-gradient-to-r from-slate-300 to-slate-400 text-slate-900", icon: Star };
      case "gold":
        return { label: "Gold", color: "bg-gradient-to-r from-yellow-400 to-yellow-500 text-yellow-900", icon: Award };
      case "silver":
        return { label: "Silver", color: "bg-gradient-to-r from-slate-400 to-slate-500 text-slate-900", icon: Award };
      case "partner":
        return { label: "Partner", color: "bg-primary text-primary-foreground", icon: Handshake };
      default:
        return { label: tier, color: "bg-secondary", icon: Handshake };
    }
  };

  const groupedSponsors = {
    platinum: sponsors.filter((s) => s.tier === "platinum"),
    gold: sponsors.filter((s) => s.tier === "gold"),
    silver: sponsors.filter((s) => s.tier === "silver"),
    partner: sponsors.filter((s) => s.tier === "partner"),
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="relative bg-background border-b py-16">
          <div className="container mx-auto px-4 lg:px-8">
            <h1 className="text-4xl md:text-5xl font-bold mb-4" data-testid="text-sponsors-title">
              Our Sponsors & Partners
            </h1>
            <p className="text-xl text-muted-foreground max-w-3xl">
              We're proud to work with the best brands and organizations in UK fishing. Their support makes Peg Slam competitions possible.
            </p>
          </div>
        </div>

        <div className="container mx-auto px-4 lg:px-8 py-12">
          <div className="mb-16">
            <div className="flex items-center gap-2 mb-6">
              <Star className="h-6 w-6 text-primary" />
              <h2 className="text-3xl font-bold">Platinum Sponsors</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {[1, 2].map((i) => (
                <Card key={i} className="overflow-hidden">
                  <CardHeader>
                    <Skeleton className="h-16 w-32 mb-4" />
                    <Skeleton className="h-8 w-3/4 mb-2" />
                    <Skeleton className="h-4 w-full mb-1" />
                    <Skeleton className="h-4 w-5/6" />
                  </CardHeader>
                  <CardContent>
                    <Skeleton className="h-10 w-full" />
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (sponsors.length === 0) {
    return (
      <div className="min-h-screen bg-background">
        <div className="relative bg-background border-b py-16">
          <div className="container mx-auto px-4 lg:px-8">
            <h1 className="text-4xl md:text-5xl font-bold mb-4" data-testid="text-sponsors-title">
              Our Sponsors & Partners
            </h1>
            <p className="text-xl text-muted-foreground max-w-3xl">
              We're proud to work with the best brands and organizations in UK fishing. Their support makes Peg Slam competitions possible.
            </p>
          </div>
        </div>

        <div className="container mx-auto px-4 lg:px-8 py-12">
          <div className="text-center py-16">
            <Handshake className="h-16 w-16 mx-auto text-muted-foreground mb-4" />
            <h3 className="text-xl font-semibold mb-2">No sponsors yet</h3>
            <p className="text-muted-foreground">Check back soon for our amazing sponsors and partners</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="relative bg-background border-b py-16">
        <div className="container mx-auto px-4 lg:px-8">
          <h1 className="text-4xl md:text-5xl font-bold mb-4" data-testid="text-sponsors-title">
            Our Sponsors & Partners
          </h1>
          <p className="text-xl text-muted-foreground max-w-3xl">
            We're proud to work with the best brands and organizations in UK fishing. Their support makes Peg Slam competitions possible.
          </p>
        </div>
      </div>

      <div className="container mx-auto px-4 lg:px-8 py-12">
        {groupedSponsors.platinum.length > 0 && (
          <div className="mb-16">
            <div className="flex items-center gap-2 mb-6">
              <Star className="h-6 w-6 text-primary" />
              <h2 className="text-3xl font-bold">Platinum Sponsors</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {groupedSponsors.platinum.map((sponsor) => {
                const tierInfo = getTierInfo(sponsor.tier);
                const TierIcon = tierInfo.icon;

                return (
                  <Card key={sponsor.id} className="overflow-hidden" data-testid={`card-sponsor-${sponsor.id}`}>
                    <CardHeader>
                      <div className="flex items-start justify-between gap-4 mb-4">
                        <div className="h-16 w-32 bg-muted rounded flex items-center justify-center">
                          {sponsor.logo ? (
                            <img src={sponsor.logo} alt={sponsor.name} className="h-full w-full object-contain" />
                          ) : (
                            <span className="text-2xl font-bold text-muted-foreground">{sponsor.name[0]}</span>
                          )}
                        </div>
                        <Badge className={tierInfo.color}>
                          <TierIcon className="h-3 w-3 mr-1" />
                          {tierInfo.label}
                        </Badge>
                      </div>
                      <CardTitle className="text-2xl" data-testid={`text-sponsor-name-${sponsor.id}`}>
                        {sponsor.name}
                      </CardTitle>
                      <CardDescription className="text-base">{sponsor.description}</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="flex flex-wrap items-center gap-3">
                        {sponsor.website && (
                          <Button asChild data-testid={`button-sponsor-website-${sponsor.id}`}>
                            <a href={sponsor.website} target="_blank" rel="noopener noreferrer">
                              Visit Website
                              <ExternalLink className="ml-2 h-4 w-4" />
                            </a>
                          </Button>
                        )}
                        {sponsor.social && (
                          <div className="flex gap-2">
                            {sponsor.social.facebook && (
                              <Button variant="outline" size="icon" asChild>
                                <a href={sponsor.social.facebook} target="_blank" rel="noopener noreferrer" data-testid={`button-sponsor-facebook-${sponsor.id}`}>
                                  <SiFacebook className="h-4 w-4" />
                                </a>
                              </Button>
                            )}
                            {sponsor.social.twitter && (
                              <Button variant="outline" size="icon" asChild>
                                <a href={sponsor.social.twitter} target="_blank" rel="noopener noreferrer" data-testid={`button-sponsor-twitter-${sponsor.id}`}>
                                  <SiX className="h-4 w-4" />
                                </a>
                              </Button>
                            )}
                            {sponsor.social.instagram && (
                              <Button variant="outline" size="icon" asChild>
                                <a href={sponsor.social.instagram} target="_blank" rel="noopener noreferrer" data-testid={`button-sponsor-instagram-${sponsor.id}`}>
                                  <SiInstagram className="h-4 w-4" />
                                </a>
                              </Button>
                            )}
                          </div>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          </div>
        )}

        {groupedSponsors.gold.length > 0 && (
          <div className="mb-16">
            <div className="flex items-center gap-2 mb-6">
              <Award className="h-6 w-6 text-yellow-500" />
              <h2 className="text-3xl font-bold">Gold Sponsors</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {groupedSponsors.gold.map((sponsor) => {
                const tierInfo = getTierInfo(sponsor.tier);
                const TierIcon = tierInfo.icon;

                return (
                  <Card key={sponsor.id} className="hover-elevate" data-testid={`card-sponsor-${sponsor.id}`}>
                    <CardHeader>
                      <div className="flex items-center justify-between mb-3">
                        <div className="h-12 w-24 bg-muted rounded flex items-center justify-center">
                          {sponsor.logo ? (
                            <img src={sponsor.logo} alt={sponsor.name} className="h-full w-full object-contain" />
                          ) : (
                            <span className="text-xl font-bold text-muted-foreground">{sponsor.name[0]}</span>
                          )}
                        </div>
                        <Badge className={tierInfo.color}>
                          <TierIcon className="h-3 w-3 mr-1" />
                          {tierInfo.label}
                        </Badge>
                      </div>
                      <CardTitle data-testid={`text-sponsor-name-${sponsor.id}`}>{sponsor.name}</CardTitle>
                      <CardDescription>{sponsor.description}</CardDescription>
                    </CardHeader>
                    <CardContent>
                      {sponsor.website && (
                        <Button variant="outline" className="w-full" asChild data-testid={`button-sponsor-website-${sponsor.id}`}>
                          <a href={sponsor.website} target="_blank" rel="noopener noreferrer">
                            Visit Website
                            <ExternalLink className="ml-2 h-4 w-4" />
                          </a>
                        </Button>
                      )}
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          </div>
        )}

        {groupedSponsors.silver.length > 0 && (
          <div className="mb-16">
            <div className="flex items-center gap-2 mb-6">
              <Award className="h-6 w-6 text-slate-400" />
              <h2 className="text-3xl font-bold">Silver Sponsors</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {groupedSponsors.silver.map((sponsor) => {
                const tierInfo = getTierInfo(sponsor.tier);
                const TierIcon = tierInfo.icon;

                return (
                  <Card key={sponsor.id} className="hover-elevate" data-testid={`card-sponsor-${sponsor.id}`}>
                    <CardHeader>
                      <div className="flex items-center justify-between mb-3">
                        <div className="h-12 w-24 bg-muted rounded flex items-center justify-center">
                          {sponsor.logo ? (
                            <img src={sponsor.logo} alt={sponsor.name} className="h-full w-full object-contain" />
                          ) : (
                            <span className="text-xl font-bold text-muted-foreground">{sponsor.name[0]}</span>
                          )}
                        </div>
                        <Badge className={tierInfo.color}>
                          <TierIcon className="h-3 w-3 mr-1" />
                          {tierInfo.label}
                        </Badge>
                      </div>
                      <CardTitle data-testid={`text-sponsor-name-${sponsor.id}`}>{sponsor.name}</CardTitle>
                      <CardDescription>{sponsor.description}</CardDescription>
                    </CardHeader>
                    <CardContent>
                      {sponsor.website && (
                        <Button variant="outline" className="w-full" asChild data-testid={`button-sponsor-website-${sponsor.id}`}>
                          <a href={sponsor.website} target="_blank" rel="noopener noreferrer">
                            Visit Website
                            <ExternalLink className="ml-2 h-4 w-4" />
                          </a>
                        </Button>
                      )}
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          </div>
        )}

        {groupedSponsors.partner.length > 0 && (
          <div>
            <div className="flex items-center gap-2 mb-6">
              <Handshake className="h-6 w-6 text-primary" />
              <h2 className="text-3xl font-bold">Official Partners</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {groupedSponsors.partner.map((sponsor) => (
                <Card key={sponsor.id} className="hover-elevate" data-testid={`card-sponsor-${sponsor.id}`}>
                  <CardHeader>
                    <div className="h-12 w-24 bg-muted rounded flex items-center justify-center mb-3">
                      {sponsor.logo ? (
                        <img src={sponsor.logo} alt={sponsor.name} className="h-full w-full object-contain" />
                      ) : (
                        <span className="text-xl font-bold text-muted-foreground">{sponsor.name[0]}</span>
                      )}
                    </div>
                    <CardTitle className="text-lg" data-testid={`text-sponsor-name-${sponsor.id}`}>
                      {sponsor.name}
                    </CardTitle>
                    <CardDescription className="text-sm">{sponsor.description}</CardDescription>
                  </CardHeader>
                  <CardContent>
                    {sponsor.website && (
                      <Button variant="ghost" className="w-full" asChild data-testid={`button-sponsor-website-${sponsor.id}`}>
                        <a href={sponsor.website} target="_blank" rel="noopener noreferrer">
                          Learn More
                          <ExternalLink className="ml-2 h-4 w-4" />
                        </a>
                      </Button>
                    )}
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

