import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Calendar, Clock, Search, ArrowRight, Trophy, Newspaper, Share2 } from "lucide-react";
import { SiFacebook, SiX } from "react-icons/si";
import { FaWhatsapp } from "react-icons/fa";
import type { News } from "@shared/schema";

export default function News() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [selectedArticle, setSelectedArticle] = useState<News | null>(null);
  const { toast } = useToast();

  const { data: newsArticles = [], isLoading } = useQuery<News[]>({
    queryKey: ["/api/news"],
  });

  const filteredArticles = newsArticles.filter((article) => {
    const matchesSearch = article.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      article.excerpt.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === "all" || article.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const getCategoryBadge = (category: string) => {
    switch (category) {
      case "match-report":
        return { label: "Match Report", variant: "default" as const, icon: Trophy };
      case "announcement":
        return { label: "Announcement", variant: "secondary" as const, icon: Newspaper };
      case "news":
        return { label: "News", variant: "outline" as const, icon: Newspaper };
      default:
        return { label: category, variant: "outline" as const, icon: Newspaper };
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-4xl md:text-5xl font-bold mb-4" data-testid="text-news-title">
            News & Updates
          </h1>
          <p className="text-lg text-muted-foreground">
            Stay up to date with match reports, announcements, and the latest from the UK fishing competition scene
          </p>
        </div>

        <div className="mb-8 flex flex-col sm:flex-row gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              type="text"
              placeholder="Search articles..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
              data-testid="input-news-search"
            />
          </div>
          <div className="flex gap-2 flex-wrap">
            <Button
              variant={selectedCategory === "all" ? "default" : "outline"}
              onClick={() => setSelectedCategory("all")}
              data-testid="button-filter-all"
            >
              All
            </Button>
            <Button
              variant={selectedCategory === "match-report" ? "default" : "outline"}
              onClick={() => setSelectedCategory("match-report")}
              data-testid="button-filter-match-reports"
            >
              <Trophy className="h-4 w-4 mr-2" />
              Match Reports
            </Button>
            <Button
              variant={selectedCategory === "announcement" ? "default" : "outline"}
              onClick={() => setSelectedCategory("announcement")}
              data-testid="button-filter-announcements"
            >
              Announcements
            </Button>
            <Button
              variant={selectedCategory === "news" ? "default" : "outline"}
              onClick={() => setSelectedCategory("news")}
              data-testid="button-filter-news"
            >
              News
            </Button>
          </div>
        </div>

        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[1, 2, 3, 4, 5, 6].map((i) => (
              <Card key={i} className="flex flex-col overflow-hidden">
                <Skeleton className="aspect-video w-full" />
                <CardHeader>
                  <Skeleton className="h-6 w-3/4 mb-2" />
                  <Skeleton className="h-4 w-full" />
                  <Skeleton className="h-4 w-5/6" />
                </CardHeader>
                <CardContent>
                  <Skeleton className="h-4 w-full mb-2" />
                  <Skeleton className="h-4 w-4/5" />
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredArticles.map((article) => {
                const categoryInfo = getCategoryBadge(article.category);
                const CategoryIcon = categoryInfo.icon;

                return (
                  <Card key={article.id} className="flex flex-col overflow-hidden hover-elevate" data-testid={`card-news-${article.id}`}>
                    <div className="relative aspect-video overflow-hidden">
                      <img
                        src={article.image}
                        alt={article.title}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute top-2 left-2">
                        <Badge variant={categoryInfo.variant}>
                          <CategoryIcon className="h-3 w-3 mr-1" />
                          {categoryInfo.label}
                        </Badge>
                      </div>
                    </div>
                    <CardHeader>
                      <h3 className="text-xl font-semibold line-clamp-2" data-testid={`text-news-title-${article.id}`}>
                        {article.title}
                      </h3>
                    </CardHeader>
                    <CardContent className="flex-1">
                      <p className="text-muted-foreground line-clamp-3">{article.excerpt}</p>
                    </CardContent>
                    <CardFooter className="flex flex-wrap items-center justify-between gap-4 pt-0">
                      <div className="flex flex-wrap items-center gap-4 text-sm text-muted-foreground">
                        <div className="flex items-center gap-1">
                          <Calendar className="h-3 w-3" />
                          <span>{article.date}</span>
                        </div>
                        <div className="flex items-center gap-1">
                          <Clock className="h-3 w-3" />
                          <span>{article.readTime}</span>
                        </div>
                      </div>
                      <Button 
                        variant="ghost" 
                        size="sm" 
                        onClick={() => setSelectedArticle(article)}
                        data-testid={`button-read-more-${article.id}`}
                      >
                        Read More
                        <ArrowRight className="ml-2 h-4 w-4" />
                      </Button>
                    </CardFooter>
                  </Card>
                );
              })}
            </div>

            {filteredArticles.length === 0 && (
              <div className="text-center py-16">
                <Newspaper className="h-16 w-16 mx-auto text-muted-foreground mb-4" />
                <h3 className="text-xl font-semibold mb-2">No articles found</h3>
                <p className="text-muted-foreground">
                  {searchQuery ? "Try adjusting your search query" : "Check back soon for new updates"}
                </p>
              </div>
            )}
          </>
        )}
      </div>

      <Dialog open={!!selectedArticle} onOpenChange={(open) => !open && setSelectedArticle(null)}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto" data-testid="dialog-news-detail">
          {selectedArticle && (
            <>
              <DialogHeader>
                <DialogTitle data-testid="text-dialog-news-title">{selectedArticle.title}</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div className="relative aspect-video overflow-hidden rounded-md">
                  <img
                    src={selectedArticle.image}
                    alt={selectedArticle.title}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="flex flex-wrap items-center gap-4">
                  {(() => {
                    const categoryInfo = getCategoryBadge(selectedArticle.category);
                    const CategoryIcon = categoryInfo.icon;
                    return (
                      <Badge variant={categoryInfo.variant}>
                        <CategoryIcon className="h-3 w-3 mr-1" />
                        {categoryInfo.label}
                      </Badge>
                    );
                  })()}
                  <div className="flex items-center gap-4 text-sm text-muted-foreground">
                    <div className="flex items-center gap-1">
                      <Calendar className="h-3 w-3" />
                      <span data-testid="text-dialog-news-date">{selectedArticle.date}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <Clock className="h-3 w-3" />
                      <span>{selectedArticle.readTime}</span>
                    </div>
                  </div>
                </div>
                <div className="space-y-3">
                  <p className="text-muted-foreground font-medium">{selectedArticle.excerpt}</p>
                  <div className="prose prose-sm max-w-none dark:prose-invert" data-testid="text-dialog-news-content" dangerouslySetInnerHTML={{ __html: selectedArticle.content }} />
                  <div className="flex items-center gap-4 text-sm pt-4 border-t">
                    <span className="text-muted-foreground">By {selectedArticle.author}</span>
                    {selectedArticle.competition && (
                      <>
                        <span className="text-muted-foreground">•</span>
                        <div className="flex items-center gap-2">
                          <Trophy className="h-4 w-4 text-muted-foreground" />
                          <span data-testid="text-dialog-news-competition">{selectedArticle.competition}</span>
                        </div>
                      </>
                    )}
                  </div>
                  <div className="flex items-center gap-2 pt-4 border-t mt-4">
                    <Share2 className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm text-muted-foreground mr-2">Share:</span>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => {
                        const url = window.location.href;
                        const text = selectedArticle.title;
                        window.open(`https://wa.me/?text=${encodeURIComponent(text + ' - ' + url)}`, '_blank');
                      }}
                      data-testid="button-share-whatsapp"
                    >
                      <FaWhatsapp className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => {
                        const url = window.location.href;
                        window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`, '_blank');
                      }}
                      data-testid="button-share-facebook"
                    >
                      <SiFacebook className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => {
                        const url = window.location.href;
                        const text = selectedArticle.title;
                        window.open(`https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}&url=${encodeURIComponent(url)}`, '_blank');
                      }}
                      data-testid="button-share-x"
                    >
                      <SiX className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        navigator.clipboard.writeText(window.location.href);
                        toast({
                          title: "Link copied",
                          description: "Link copied to clipboard for sharing on Instagram",
                        });
                      }}
                      data-testid="button-share-instagram"
                    >
                      Copy Link
                    </Button>
                  </div>
                </div>
              </div>
            </>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useEffect, useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { 
  User as UserIcon, Trophy, Calendar, MapPin, Fish, TrendingUp, 
  Settings, Edit, Award, Target, Loader2, Upload, Trash2, Image as ImageIcon
} from "lucide-react";
import { Link, useRoute, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { User, Competition, CompetitionParticipant, UserGalleryPhoto } from "@shared/schema";
import { EditProfileDialog } from "@/components/edit-profile-dialog";

export default function Profile() {
  const [, params] = useRoute("/profile/:username");
  const [, setLocation] = useLocation();
  const { user: loggedInUser, isLoading: authLoading, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const [editProfileOpen, setEditProfileOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [caption, setCaption] = useState("");
  const [isUploading, setIsUploading] = useState(false);

  const viewingUsername = params?.username;
  const isOwnProfile = !viewingUsername || viewingUsername === loggedInUser?.username;

  const { data: profileUser, isLoading: profileLoading } = useQuery<User>({
    queryKey: [`/api/users/${viewingUsername}`],
    enabled: !!viewingUsername && !isOwnProfile,
  });

  const { data: participations = [], isLoading: participationsLoading } = useQuery<Array<CompetitionParticipant & { competition: Competition }>>({
    queryKey: ["/api/user/participations"],
    enabled: isOwnProfile && isAuthenticated,
  });

  const { data: stats } = useQuery<{
    wins: number;
    podiumFinishes: number;
    bestCatch: string;
    averageWeight: string;
    totalWeight: string;
    totalCompetitions: number;
  }>({
    queryKey: ["/api/user/stats"],
    enabled: isOwnProfile && isAuthenticated,
  });

  const { data: galleryPhotos = [] } = useQuery<UserGalleryPhoto[]>({
    queryKey: ["/api/user/gallery"],
    enabled: isOwnProfile && isAuthenticated,
  });

  const addPhotoMutation = useMutation({
    mutationFn: async (data: { url: string; caption?: string }) => {
      const response = await apiRequest("POST", "/api/user/gallery", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user/gallery"] });
      toast({
        title: "Photo added",
        description: "Your photo has been added to your gallery.",
      });
      setSelectedFile(null);
      setCaption("");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to add photo",
        variant: "destructive",
      });
    },
  });

  const deletePhotoMutation = useMutation({
    mutationFn: async (photoId: string) => {
      const response = await apiRequest("DELETE", `/api/user/gallery/${photoId}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user/gallery"] });
      toast({
        title: "Photo deleted",
        description: "Your photo has been removed from your gallery.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete photo",
        variant: "destructive",
      });
    },
  });

  const handleAddPhoto = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedFile) {
      toast({
        title: "Error",
        description: "Please select a photo to upload",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsUploading(true);

      // Upload the file first
      const formData = new FormData();
      formData.append('image', selectedFile);
      formData.append('type', 'gallery');

      const uploadResponse = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (!uploadResponse.ok) {
        const errorData = await uploadResponse.json();
        throw new Error(errorData.message || 'Failed to upload image');
      }

      const uploadData = await uploadResponse.json();

      // Now add the photo to gallery with the uploaded URL
      addPhotoMutation.mutate({ 
        url: uploadData.url, 
        caption: caption || undefined 
      });
    } catch (error: any) {
      toast({
        title: "Upload failed",
        description: error.message || "Failed to upload photo",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  useEffect(() => {
    if (!authLoading && !isAuthenticated && isOwnProfile) {
      setLocation("/login");
    }
  }, [authLoading, isAuthenticated, isOwnProfile, setLocation]);

  if (authLoading || (profileLoading && !isOwnProfile)) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  if (!loggedInUser && isOwnProfile) {
    return null;
  }

  const displayUser = isOwnProfile ? loggedInUser : profileUser;

  if (!displayUser) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-2">User not found</h2>
          <p className="text-muted-foreground mb-4">The user you're looking for doesn't exist.</p>
          <Link href="/">
            <Button>Go Home</Button>
          </Link>
        </div>
      </div>
    );
  }

  const getPositionBadge = (position: number) => {
    if (position === 1) return { variant: "default" as const, color: "text-yellow-500", label: "1st" };
    if (position === 2) return { variant: "secondary" as const, color: "text-slate-400", label: "2nd" };
    if (position === 3) return { variant: "secondary" as const, color: "text-amber-600", label: "3rd" };
    return { variant: "outline" as const, color: "", label: `${position}th` };
  };

  // Helper function to compute competition status based on date and time
  const getCompetitionStatus = (comp: Competition): string => {
    const now = new Date();
    const compDateTime = new Date(`${comp.date}T${comp.time}`);

    // If competition date/time has passed, it's completed
    if (compDateTime < now) {
      return "completed";
    }

    // If competition is within the next 24 hours, it's live
    const hoursUntilComp = (compDateTime.getTime() - now.getTime()) / (1000 * 60 * 60);
    if (hoursUntilComp <= 24 && hoursUntilComp >= 0) {
      return "live";
    }

    // Otherwise, it's upcoming
    return "upcoming";
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 lg:px-8 py-8">
        <Card className="mb-8">
          <CardContent className="pt-6">
            <div className="flex flex-col md:flex-row gap-6">
              <div className="flex flex-col items-center md:items-start">
                <Avatar className="h-32 w-32 mb-4" data-testid="avatar-profile">
                  <AvatarFallback className="text-3xl">
                    {displayUser.firstName[0]}{displayUser.lastName[0]}
                  </AvatarFallback>
                </Avatar>
                {isOwnProfile && (
                  <Button 
                    variant="outline" 
                    className="w-full md:w-auto" 
                    onClick={() => setEditProfileOpen(true)}
                    data-testid="button-edit-profile"
                  >
                    <Edit className="h-4 w-4 mr-2" />
                    Edit Profile
                  </Button>
                )}
              </div>

              <div className="flex-1">
                <div className="flex flex-col sm:flex-row sm:items-start sm:justify-between gap-4 mb-4">
                  <div>
                    <h1 className="text-3xl font-bold mb-1" data-testid="text-profile-name">
                      {displayUser.firstName} {displayUser.lastName}
                    </h1>
                    <p className="text-muted-foreground">@{displayUser.username}</p>
                  </div>
                  {isOwnProfile && (
                    <Button variant="outline" size="icon" data-testid="button-settings">
                      <Settings className="h-4 w-4" />
                    </Button>
                  )}
                </div>

                {displayUser.bio && <p className="text-muted-foreground mb-4">{displayUser.bio}</p>}

                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
                  {displayUser.club && (
                    <div className="flex items-center gap-2 text-sm">
                      <UserIcon className="h-4 w-4 text-muted-foreground" />
                      <span data-testid="text-club">{displayUser.club}</span>
                    </div>
                  )}
                  {displayUser.location && (
                    <div className="flex items-center gap-2 text-sm">
                      <MapPin className="h-4 w-4 text-muted-foreground" />
                      <span data-testid="text-location">{displayUser.location}</span>
                    </div>
                  )}
                  {displayUser.favouriteMethod && (
                    <div className="flex items-center gap-2 text-sm">
                      <Target className="h-4 w-4 text-muted-foreground" />
                      <span data-testid="text-method">{displayUser.favouriteMethod}</span>
                    </div>
                  )}
                  {displayUser.favouriteSpecies && (
                    <div className="flex items-center gap-2 text-sm">
                      <Fish className="h-4 w-4 text-muted-foreground" />
                      <span data-testid="text-species">{displayUser.favouriteSpecies}</span>
                    </div>
                  )}
                </div>

                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Calendar className="h-4 w-4" />
                  <span>Member since {new Date(displayUser.createdAt).toLocaleDateString('en-GB', { month: 'long', year: 'numeric', timeZone: 'Europe/London' })}</span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Matches</CardTitle>
              <Trophy className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-stat-total-matches">
                {isOwnProfile ? participations.length : 0}
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Wins</CardTitle>
              <Award className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-primary" data-testid="text-stat-wins">
                {isOwnProfile && stats ? stats.wins : 0}
              </div>
              <p className="text-xs text-muted-foreground">
                {isOwnProfile && stats ? stats.podiumFinishes : 0} podium finishes
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Best Catch</CardTitle>
              <Fish className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-stat-best-catch">
                {isOwnProfile && stats ? stats.bestCatch : "-"}
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Average Weight</CardTitle>
              <TrendingUp className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-stat-average">
                {isOwnProfile && stats ? stats.averageWeight : "-"}
              </div>
              <p className="text-xs text-muted-foreground">
                Total: {isOwnProfile && stats ? stats.totalWeight : "-"}
              </p>
            </CardContent>
          </Card>
        </div>

        <Tabs defaultValue="history" className="space-y-4">
          <TabsList data-testid="tabs-profile">
            <TabsTrigger value="history" data-testid="tab-history">
              <Trophy className="h-4 w-4 mr-2" />
              Match History
            </TabsTrigger>
            {isOwnProfile && (
              <TabsTrigger value="upcoming" data-testid="tab-upcoming">
                <Calendar className="h-4 w-4 mr-2" />
                Upcoming Events
              </TabsTrigger>
            )}
            {isOwnProfile && (
              <TabsTrigger value="gallery" data-testid="tab-gallery">
                <ImageIcon className="h-4 w-4 mr-2" />
                Gallery
              </TabsTrigger>
            )}
          </TabsList>

          <TabsContent value="history" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Competition History</CardTitle>
                <CardDescription>
                  All completed matches and results
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8">
                  <Trophy className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
                  <p className="text-muted-foreground mb-4">
                    No competition history yet
                  </p>
                  <Link href="/competitions">
                    <Button data-testid="button-browse-competitions">
                      Browse Competitions
                    </Button>
                  </Link>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {isOwnProfile && (
            <TabsContent value="upcoming" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle>Upcoming Competitions</CardTitle>
                  <CardDescription>
                    Competitions you've entered
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  {participationsLoading ? (
                    <div className="flex items-center justify-center py-8">
                      <Loader2 className="h-8 w-8 animate-spin text-primary" />
                    </div>
                  ) : participations.length === 0 ? (
                    <div className="text-center py-8">
                      <Calendar className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
                      <p className="text-muted-foreground mb-4">
                        No upcoming competitions booked
                      </p>
                      <Link href="/competitions">
                        <Button data-testid="button-browse-competitions">
                          Browse Competitions
                        </Button>
                      </Link>
                    </div>
                  ) : (
                    <div className="space-y-3">
                      {participations.map((participation) => (
                        <Card key={participation.id} className="hover-elevate" data-testid={`card-participation-${participation.id}`}>
                          <CardContent className="p-4">
                            <div className="flex items-center justify-between gap-4">
                              <div className="flex-1">
                                <div className="flex items-center gap-2 mb-1">
                                  <h3 className="font-semibold">{participation.competition.name}</h3>
                                  <Badge variant={getCompetitionStatus(participation.competition) === "upcoming" ? "default" : "secondary"}>
                                    {getCompetitionStatus(participation.competition)}
                                  </Badge>
                                </div>
                                <div className="flex items-center gap-4 text-sm text-muted-foreground">
                                  <div className="flex items-center gap-1">
                                    <Calendar className="h-3 w-3" />
                                    <span>{new Date(participation.competition.date).toLocaleDateString('en-GB', {
                                      day: 'numeric',
                                      month: 'short',
                                      year: 'numeric',
                                      timeZone: 'Europe/London'
                                    })}</span>
                                  </div>
                                  <div className="flex items-center gap-1">
                                    <MapPin className="h-3 w-3" />
                                    <span>{participation.competition.venue}</span>
                                  </div>
                                  <div className="flex items-center gap-1">
                                    <Target className="h-3 w-3" />
                                    <span>Peg {participation.pegNumber}</span>
                                  </div>
                                </div>
                              </div>
                              <Link href={`/competition/${participation.competition.id}`}>
                                <Button variant="outline" size="sm">
                                  View Details
                                </Button>
                              </Link>
                            </div>
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>
          )}

          {isOwnProfile && (
            <TabsContent value="gallery" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle>My Gallery</CardTitle>
                  <CardDescription>
                    Upload and manage your fishing photos
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <form onSubmit={handleAddPhoto} className="space-y-4 mb-6">
                    <div className="space-y-2">
                      <Label htmlFor="photoFile">Choose Photo</Label>
                      <Input
                        id="photoFile"
                        type="file"
                        accept="image/*"
                        onChange={(e) => setSelectedFile(e.target.files?.[0] || null)}
                        data-testid="input-photo-file"
                      />
                      {selectedFile && (
                        <p className="text-sm text-muted-foreground">
                          Selected: {selectedFile.name}
                        </p>
                      )}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="caption">Caption (optional)</Label>
                      <Input
                        id="caption"
                        placeholder="Add a caption..."
                        value={caption}
                        onChange={(e) => setCaption(e.target.value)}
                        data-testid="input-caption"
                      />
                    </div>
                    <Button
                      type="submit"
                      disabled={isUploading || addPhotoMutation.isPending}
                      data-testid="button-add-photo"
                    >
                      {(isUploading || addPhotoMutation.isPending) ? (
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      ) : (
                        <Upload className="mr-2 h-4 w-4" />
                      )}
                      {isUploading ? 'Uploading...' : 'Add Photo'}
                    </Button>
                  </form>

                  {galleryPhotos.length === 0 ? (
                    <div className="text-center py-12">
                      <ImageIcon className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
                      <p className="text-muted-foreground mb-4">
                        No photos in your gallery yet
                      </p>
                      <p className="text-sm text-muted-foreground">
                        Add your first photo to showcase your catches!
                      </p>
                    </div>
                  ) : (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                      {galleryPhotos.map((photo) => (
                        <Card key={photo.id} className="overflow-hidden" data-testid={`card-photo-${photo.id}`}>
                          <div className="aspect-square relative bg-muted">
                            <img
                              src={photo.url}
                              alt={photo.caption || "Gallery photo"}
                              className="w-full h-full object-cover"
                            />
                          </div>
                          {photo.caption && (
                            <CardContent className="pt-3">
                              <p className="text-sm">{photo.caption}</p>
                            </CardContent>
                          )}
                          <CardContent className="pt-2 pb-3">
                            <Button
                              variant="destructive"
                              size="sm"
                              className="w-full"
                              onClick={() => deletePhotoMutation.mutate(photo.id)}
                              disabled={deletePhotoMutation.isPending}
                              data-testid={`button-delete-${photo.id}`}
                            >
                              <Trash2 className="h-4 w-4 mr-2" />
                              Delete
                            </Button>
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>
          )}
        </Tabs>
      </div>

      {isOwnProfile && loggedInUser && (
        <EditProfileDialog
          open={editProfileOpen}
          onOpenChange={setEditProfileOpen}
          user={loggedInUser}
        />
      )}
    </div>
  );
}

client/src/pages/admin-news.tsx.content:
import { useState, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import type { News } from "@shared/schema";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Plus, Pencil, Trash2, Trophy, Newspaper, Search, Upload } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

export default function AdminNews() {
  const { toast } = useToast();
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [isEditOpen, setIsEditOpen] = useState(false);
  const [selectedArticle, setSelectedArticle] = useState<News | null>(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [categoryFilter, setCategoryFilter] = useState<"all" | "match-report" | "announcement" | "news">("all");
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [uploadingImage, setUploadingImage] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const [formData, setFormData] = useState({
    title: "",
    excerpt: "",
    content: "",
    category: "",
    author: "",
    date: "",
    readTime: "",
    image: "",
    competition: "",
  });

  const { data: articles = [], isLoading } = useQuery<News[]>({
    queryKey: ["/api/news"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/admin/news", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/news"] });
      setIsCreateOpen(false);
      resetForm();
      toast({
        title: "Article published",
        description: `${formData.title} has been published successfully.`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await apiRequest("PUT", `/api/admin/news/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/news"] });
      setIsEditOpen(false);
      setSelectedArticle(null);
      resetForm();
      toast({
        title: "Article updated",
        description: `${formData.title} has been updated successfully.`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest("DELETE", `/api/admin/news/${id}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/news"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const uploadFile = async (file: File): Promise<string> => {
    const formData = new FormData();
    formData.append('image', file);
    formData.append('type', 'news');

    const response = await fetch('/api/upload', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      throw new Error('File upload failed');
    }

    const data = await response.json();
    return data.url;
  };

  const handleCreate = async () => {
    try {
      let imageUrl = formData.image;

      if (imageFile) {
        setUploadingImage(true);
        imageUrl = await uploadFile(imageFile);
      }

      const newsData = {
        title: formData.title,
        excerpt: formData.excerpt,
        content: formData.content,
        category: formData.category,
        author: formData.author,
        date: formData.date,
        readTime: formData.readTime,
        image: imageUrl,
        competition: formData.competition || undefined,
      };

      createMutation.mutate(newsData);
      setImageFile(null);
      if (fileInputRef.current) fileInputRef.current.value = '';
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to upload image",
        variant: "destructive",
      });
    } finally {
      setUploadingImage(false);
    }
  };

  const handleEdit = async () => {
    if (!selectedArticle) return;

    try {
      let imageUrl = formData.image;

      if (imageFile) {
        setUploadingImage(true);
        imageUrl = await uploadFile(imageFile);
      }

      const newsData = {
        title: formData.title,
        excerpt: formData.excerpt,
        content: formData.content,
        category: formData.category,
        author: formData.author,
        date: formData.date,
        readTime: formData.readTime,
        image: imageUrl,
        competition: formData.competition || undefined,
      };

      updateMutation.mutate({ id: selectedArticle.id, data: newsData });
      setImageFile(null);
      if (fileInputRef.current) fileInputRef.current.value = '';
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to upload image",
        variant: "destructive",
      });
    } finally {
      setUploadingImage(false);
    }
  };

  const handleDelete = (id: string, title: string) => {
    deleteMutation.mutate(id);
    toast({
      title: "Article deleted",
      description: `${title} has been removed.`,
    });
  };

  const openEditDialog = (article: News) => {
    setSelectedArticle(article);
    setFormData({
      title: article.title,
      excerpt: article.excerpt,
      content: article.content,
      category: article.category,
      author: article.author,
      date: article.date,
      readTime: article.readTime,
      image: article.image,
      competition: article.competition || "",
    });
    setIsEditOpen(true);
  };

  const resetForm = () => {
    setFormData({
      title: "",
      excerpt: "",
      content: "",
      category: "",
      author: "",
      date: "",
      readTime: "",
      image: "",
      competition: "",
    });
  };

  const filteredArticles = articles.filter((article) => {
    const matchesSearch =
      article.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      article.excerpt.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = categoryFilter === "all" || article.category === categoryFilter;
    return matchesSearch && matchesCategory;
  });

  const getCategoryBadgeVariant = (category: string) => {
    switch (category) {
      case "match-report":
        return "default";
      case "announcement":
        return "secondary";
      case "news":
        return "outline";
      default:
        return "secondary";
    }
  };

  const getCategoryIcon = (category: string) => {
    return category === "match-report" ? Trophy : Newspaper;
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-muted-foreground">Loading news articles...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">News Management</h2>
          <p className="text-muted-foreground">
            Create and manage news articles and announcements
          </p>
        </div>
        <Button onClick={() => setIsCreateOpen(true)} data-testid="button-create-news">
          <Plus className="h-4 w-4 mr-2" />
          Create Article
        </Button>
      </div>

      <div className="flex flex-col sm:flex-row gap-4">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search articles..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-news"
          />
        </div>
        <div className="flex gap-2">
          <Button
            variant={categoryFilter === "all" ? "default" : "outline"}
            onClick={() => setCategoryFilter("all")}
            size="sm"
            data-testid="filter-all-news"
          >
            All
          </Button>
          <Button
            variant={categoryFilter === "match-report" ? "default" : "outline"}
            onClick={() => setCategoryFilter("match-report")}
            size="sm"
            data-testid="filter-match-reports"
          >
            <Trophy className="h-4 w-4 mr-1" />
            Reports
          </Button>
          <Button
            variant={categoryFilter === "announcement" ? "default" : "outline"}
            onClick={() => setCategoryFilter("announcement")}
            size="sm"
            data-testid="filter-announcements"
          >
            Announcements
          </Button>
          <Button
            variant={categoryFilter === "news" ? "default" : "outline"}
            onClick={() => setCategoryFilter("news")}
            size="sm"
            data-testid="filter-news"
          >
            News
          </Button>
        </div>
      </div>

      <Card>
        <CardContent className="p-0">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Article</TableHead>
                <TableHead>Category</TableHead>
                <TableHead>Author</TableHead>
                <TableHead>Date</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredArticles.map((article) => {
                const CategoryIcon = getCategoryIcon(article.category);
                return (
                  <TableRow key={article.id}>
                    <TableCell>
                      <div className="flex items-start gap-3">
                        <div className="w-20 h-14 rounded overflow-hidden bg-muted flex-shrink-0">
                          <img
                            src={article.image}
                            alt={article.title}
                            className="w-full h-full object-cover"
                          />
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="font-medium line-clamp-1">{article.title}</div>
                          <div className="text-sm text-muted-foreground line-clamp-1">
                            {article.excerpt}
                          </div>
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge variant={getCategoryBadgeVariant(article.category)}>
                        <CategoryIcon className="h-3 w-3 mr-1" />
                        {article.category === "match-report"
                          ? "Match Report"
                          : article.category.charAt(0).toUpperCase() + article.category.slice(1)}
                      </Badge>
                    </TableCell>
                    <TableCell>{article.author}</TableCell>
                    <TableCell>
                      {new Date(article.date).toLocaleDateString('en-GB', { timeZone: 'Europe/London' })}
                    </TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => openEditDialog(article)}
                          data-testid={`button-edit-news-${article.id}`}
                        >
                          <Pencil className="h-3 w-3" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleDelete(article.id, article.title)}
                          data-testid={`button-delete-news-${article.id}`}
                        >
                          <Trash2 className="h-3 w-3" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {filteredArticles.length === 0 && (
        <div className="text-center py-12 text-muted-foreground">
          No articles found matching your criteria
        </div>
      )}

      <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
        <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Create News Article</DialogTitle>
            <DialogDescription>
              Publish a new article, announcement or match report
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="grid gap-2">
              <Label htmlFor="title">Title</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="Spring Qualifier Results..."
                data-testid="input-title"
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="grid gap-2">
                <Label htmlFor="category">Category</Label>
                <Select
                  value={formData.category}
                  onValueChange={(value) => setFormData({ ...formData, category: value })}
                >
                  <SelectTrigger data-testid="select-category">
                    <SelectValue placeholder="Select category" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="match-report">Match Report</SelectItem>
                    <SelectItem value="announcement">Announcement</SelectItem>
                    <SelectItem value="news">News</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="author">Author</Label>
                <Input
                  id="author"
                  value={formData.author}
                  onChange={(e) => setFormData({ ...formData, author: e.target.value })}
                  placeholder="John Smith"
                  data-testid="input-author"
                />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="grid gap-2">
                <Label htmlFor="date">Publish Date</Label>
                <Input
                  id="date"
                  type="date"
                  value={formData.date}
                  onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                  data-testid="input-date"
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="readTime">Read Time</Label>
                <Input
                  id="readTime"
                  value={formData.readTime}
                  onChange={(e) => setFormData({ ...formData, readTime: e.target.value })}
                  placeholder="5 min read"
                  data-testid="input-read-time"
                />
              </div>
            </div>
            <div className="grid gap-2">
              <Label htmlFor="competition">Competition (optional)</Label>
              <Input
                id="competition"
                value={formData.competition}
                onChange={(e) => setFormData({ ...formData, competition: e.target.value })}
                placeholder="Spring Carp Qualifier"
                data-testid="input-competition"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="image">Upload Image</Label>
              <Input
                id="image"
                type="file"
                accept="image/*"
                onChange={(e) => setImageFile(e.target.files?.[0] || null)}
                ref={fileInputRef}
                data-testid="input-image"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="excerpt">Excerpt</Label>
              <Textarea
                id="excerpt"
                value={formData.excerpt}
                onChange={(e) => setFormData({ ...formData, excerpt: e.target.value })}
                placeholder="Brief summary of the article..."
                rows={2}
                data-testid="input-excerpt"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="content">Full Content</Label>
              <ReactQuill
                theme="snow"
                value={formData.content}
                onChange={(value) => setFormData({ ...formData, content: value })}
                placeholder="Write your article content with formatting..."
                className="bg-background"
                style={{ height: '200px', marginBottom: '50px' }}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsCreateOpen(false)} data-testid="button-cancel">
              Cancel
            </Button>
            <Button onClick={handleCreate} disabled={createMutation.isPending || uploadingImage} data-testid="button-publish">
              {uploadingImage ? "Uploading..." : createMutation.isPending ? "Publishing..." : "Publish Article"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog open={isEditOpen} onOpenChange={setIsEditOpen}>
        <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Edit Article</DialogTitle>
            <DialogDescription>
              Update article details
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="grid gap-2">
              <Label htmlFor="edit-title">Title</Label>
              <Input
                id="edit-title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                data-testid="input-edit-title"
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="grid gap-2">
                <Label htmlFor="edit-category">Category</Label>
                <Select
                  value={formData.category}
                  onValueChange={(value) => setFormData({ ...formData, category: value })}
                >
                  <SelectTrigger data-testid="select-edit-category">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="match-report">Match Report</SelectItem>
                    <SelectItem value="announcement">Announcement</SelectItem>
                    <SelectItem value="news">News</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="edit-author">Author</Label>
                <Input
                  id="edit-author"
                  value={formData.author}
                  onChange={(e) => setFormData({ ...formData, author: e.target.value })}
                  data-testid="input-edit-author"
                />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="grid gap-2">
                <Label htmlFor="edit-date">Publish Date</Label>
                <Input
                  id="edit-date"
                  type="date"
                  value={formData.date}
                  onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                  data-testid="input-edit-date"
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="edit-readTime">Read Time</Label>
                <Input
                  id="edit-readTime"
                  value={formData.readTime}
                  onChange={(e) => setFormData({ ...formData, readTime: e.target.value })}
                  data-testid="input-edit-read-time"
                />
              </div>
            </div>
            <div className="grid gap-2">
              <Label htmlFor="edit-competition">Competition (optional)</Label>
              <Input
                id="edit-competition"
                value={formData.competition}
                onChange={(e) => setFormData({ ...formData, competition: e.target.value })}
                data-testid="input-edit-competition"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="edit-image">Upload New Image (optional)</Label>
              <Input
                id="edit-image"
                type="file"
                accept="image/*"
                onChange={(e) => setImageFile(e.target.files?.[0] || null)}
                ref={fileInputRef}
                data-testid="input-edit-image"
              />
              {formData.image && !imageFile && (
                <p className="text-sm text-muted-foreground">Current: {formData.image}</p>
              )}
            </div>
            <div className="grid gap-2">
              <Label htmlFor="edit-excerpt">Excerpt</Label>
              <Textarea
                id="edit-excerpt"
                value={formData.excerpt}
                onChange={(e) => setFormData({ ...formData, excerpt: e.target.value })}
                rows={2}
                data-testid="input-edit-excerpt"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="edit-content">Full Content</Label>
              <ReactQuill
                theme="snow"
                value={formData.content}
                onChange={(value) => setFormData({ ...formData, content: value })}
                className="bg-background"
                style={{ height: '200px', marginBottom: '50px' }}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsEditOpen(false)} data-testid="button-cancel-edit">
              Cancel
            </Button>
            <Button onClick={handleEdit} disabled={updateMutation.isPending || uploadingImage} data-testid="button-save-edit">
              {uploadingImage ? "Uploading..." : updateMutation.isPending ? "Saving..." : "Save Changes"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

