                <Input
                  id="peg-number"
                  type="number"
                  placeholder="23"
                  value={weighInForm.pegNumber}
                  onChange={(e) => setWeighInForm({ ...weighInForm, pegNumber: e.target.value })}
                  data-testid="input-peg-number"
