                  </Table>
                ) : (
                  <div className="text-sm text-muted-foreground">
                    No weights entered yet
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
          <DialogFooter>
            <Button onClick={() => setIsWeighInOpen(false)} data-testid="button-close-weigh-in">
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

